import React from 'react';

function Reports() {
  return (
    <div style={styles.pageBackground}>
      <div style={styles.header}>
        <h1 style={styles.title}>Reports</h1>
        <p style={styles.subtitle}>Generate and view various reports for SAHYatri.</p>
      </div>
      <div style={styles.content}>
        <div style={styles.reportSection}>
          <h2 style={styles.sectionTitle}>Available Reports</h2>
          <ul style={styles.reportList}>
            <li style={styles.reportItem}>Tourist Activity Report</li>
            <li style={styles.reportItem}>Incident Summary Report</li>
            <li style={styles.reportItem}>SOS Alert Report</li>
            <li style={styles.reportItem}>ID Issuance Report</li>
          </ul>
        </div>
        <div style={styles.actions}>
          <button style={styles.button}>Generate Report</button>
          <button style={styles.button}>Export Data</button>
        </div>
      </div>
    </div>
  );
}

const styles = {
  pageBackground: {
    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
    backgroundColor: '#e9eff6',
    minHeight: '100vh',
    color: '#2c3e50',
    lineHeight: '1.6',
  },
  header: {
    backgroundColor: '#003366',
    color: 'white',
    padding: '40px 20px',
    textAlign: 'center',
  },
  title: {
    fontSize: '2.5em',
    margin: '0',
  },
  subtitle: {
    fontSize: '1.2em',
    margin: '10px 0 0 0',
  },
  content: {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '30px 20px',
  },
  reportSection: {
    backgroundColor: '#ffffff',
    borderRadius: '12px',
    padding: '25px',
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.08)',
    border: '1px solid #dcdcdc',
    marginBottom: '30px',
  },
  sectionTitle: {
    fontSize: '1.8em',
    color: '#003366',
    marginBottom: '20px',
  },
  reportList: {
    listStyleType: 'none',
    padding: '0',
  },
  reportItem: {
    padding: '10px 0',
    borderBottom: '1px solid #eee',
    fontSize: '1.1em',
  },
  actions: {
    display: 'flex',
    gap: '20px',
    justifyContent: 'center',
  },
  button: {
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    padding: '12px 24px',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '1em',
    transition: 'background-color 0.3s ease',
  },
};

export default Reports;
