import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
// import Dashboard from './Dashboard.jsx';

// Mock data for demonstration
const mockTourists = [
  {
    id: 'T001',
    name: 'John Smith',
    nationality: 'United States',
    status: 'Active',
    safetyScore: 95,
    itinerary: 'Kathmandu → Pokhara → Chitwan',
    currentLocation: 'Thamel, Kathmandu',
    verification: 'Verified',
    emergencyContacts: '+1-555-0123, +1-555-0456',
    kyc: {
      passport: 'US123456789',
      visaExpiry: '2024-12-31',
      address: '123 Main St, New York, NY'
    },
    route: [
      { location: 'Tribhuvan Airport', time: '2024-01-15 10:30', status: 'Arrived' },
      { location: 'Thamel Hotel', time: '2024-01-15 12:00', status: 'Checked-in' },
      { location: 'Pashupatinath Temple', time: '2024-01-15 15:30', status: 'Visited' }
    ],
    safetyLog: [
      { date: '2024-01-15', event: 'Safe check-in completed', severity: 'info' },
      { date: '2024-01-14', event: 'Weather alert acknowledged', severity: 'warning' }
    ]
  },
  {
    id: 'T002',
    name: 'Maria Garcia',
    nationality: 'Spain',
    status: 'In-Transit',
    safetyScore: 88,
    itinerary: 'Lumbini → Kathmandu',
    currentLocation: 'Lumbini',
    verification: 'Verified',
    emergencyContacts: '+34-555-7890, +34-555-2345',
    kyc: {
      passport: 'ES987654321',
      visaExpiry: '2024-11-15',
      address: '456 Plaza Mayor, Madrid'
    },
    route: [
      { location: 'Lumbini Bus Station', time: '2024-01-15 08:00', status: 'Departed' },
      { location: 'Butwal Transit', time: '2024-01-15 11:30', status: 'In-transit' }
    ],
    safetyLog: [
      { date: '2024-01-15', event: 'Transit started', severity: 'info' }
    ]
  },
  {
    id: 'T003',
    name: 'Raj Patel',
    nationality: 'India',
    status: 'SOS',
    safetyScore: 45,
    itinerary: 'Everest Base Camp',
    currentLocation: 'Namche Bazaar',
    verification: 'Pending',
    emergencyContacts: '+91-555-6789, +91-555-0123',
    kyc: {
      passport: 'IN456789123',
      visaExpiry: '2024-10-30',
      address: '789 MG Road, Mumbai'
    },
    route: [
      { location: 'Lukla Airport', time: '2024-01-14 06:00', status: 'Arrived' },
      { location: 'Namche Bazaar', time: '2024-01-14 14:00', status: 'SOS Activated' }
    ],
    safetyLog: [
      { date: '2024-01-14', event: 'SOS Alert triggered', severity: 'critical' },
      { date: '2024-01-14', event: 'Medical assistance dispatched', severity: 'warning' }
    ]
  },
  {
    id: 'T004',
    name: 'Emma Wilson',
    nationality: 'Australia',
    status: 'Active',
    safetyScore: 92,
    itinerary: 'Pokhara Adventure',
    currentLocation: 'Phewa Lake, Pokhara',
    verification: 'Verified',
    emergencyContacts: '+61-555-3456, +61-555-7890',
    kyc: {
      passport: 'AU789123456',
      visaExpiry: '2025-01-20',
      address: '321 Queen St, Sydney'
    },
    route: [
      { location: 'Pokhara Airport', time: '2024-01-15 09:00', status: 'Arrived' },
      { location: 'Lakeside Hotel', time: '2024-01-15 10:30', status: 'Checked-in' },
      { location: 'Phewa Lake', time: '2024-01-15 14:00', status: 'Current' }
    ],
    safetyLog: [
      { date: '2024-01-15', event: 'Activity tracking active', severity: 'info' }
    ]
  }
];

function TouristsDirectory() {
  const [tourists, setTourists] = useState(mockTourists);
  const [filteredTourists, setFilteredTourists] = useState(mockTourists);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('All');
  const [areaFilter, setAreaFilter] = useState('All');
  const [scoreFilter, setScoreFilter] = useState('All');
  const [showAddModal, setShowAddModal] = useState(false);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [selectedTourist, setSelectedTourist] = useState(null);
  const [newTourist, setNewTourist] = useState({
    name: '',
    nationality: '',
    status: 'Active',
    safetyScore: 100,
    itinerary: '',
    currentLocation: '',
    emergencyContacts: ''
  });

  // Filter tourists based on search and filters
  useEffect(() => {
    let filtered = tourists;

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(tourist =>
        tourist.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        tourist.id.toLowerCase().includes(searchTerm.toLowerCase()) ||
        tourist.nationality.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Status filter
    if (statusFilter !== 'All') {
      filtered = filtered.filter(tourist => tourist.status === statusFilter);
    }

    // Area filter (based on current location)
    if (areaFilter !== 'All') {
      filtered = filtered.filter(tourist =>
        tourist.currentLocation.toLowerCase().includes(areaFilter.toLowerCase())
      );
    }

    // Safety score filter
    if (scoreFilter !== 'All') {
      const scoreRanges = {
        'High (80-100)': [80, 100],
        'Medium (50-79)': [50, 79],
        'Low (0-49)': [0, 49]
      };
      const [min, max] = scoreRanges[scoreFilter];
      filtered = filtered.filter(tourist =>
        tourist.safetyScore >= min && tourist.safetyScore <= max
      );
    }

    setFilteredTourists(filtered);
  }, [tourists, searchTerm, statusFilter, areaFilter, scoreFilter]);

  const handleRowClick = (tourist) => {
    setSelectedTourist(tourist);
    setShowDetailModal(true);
  };

  const handleAddTourist = (e) => {
    e.preventDefault();
    const tourist = {
      id: `T${String(tourists.length + 1).padStart(3, '0')}`,
      ...newTourist,
      verification: 'Pending',
      kyc: {
        passport: 'Pending',
        visaExpiry: 'Pending',
        address: 'Pending'
      },
      route: [],
      safetyLog: []
    };
    setTourists([...tourists, tourist]);
    setShowAddModal(false);
    setNewTourist({
      name: '',
      nationality: '',
      status: 'Active',
      safetyScore: 100,
      itinerary: '',
      currentLocation: '',
      emergencyContacts: ''
    });
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Active': return 'bg-green-100 text-green-800';
      case 'In-Transit': return 'bg-blue-100 text-blue-800';
      case 'SOS': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getSafetyScoreColor = (score) => {
    if (score >= 80) return 'text-green-600';
    if (score >= 50) return 'text-yellow-600';
    return 'text-red-600';
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Tourists Directory Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <div className="bg-blue-900 text-white p-2 rounded-lg mr-3">
                <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 24 24">
                  <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z"/>
                </svg>
              </div>
              <span className="text-xl font-bold text-blue-900">Tourists Directory</span>
            </div>
            <button
              onClick={() => setShowAddModal(true)}
              className="bg-blue-600 hover:bg-blue-500 text-white px-4 py-2 rounded-lg font-medium transition-colors duration-200 flex items-center"
            >
              <svg className="w-5 h-5 mr-2" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M10 3a1 1 0 011 1v5h5a1 1 0 110 2h-5v5a1 1 0 11-2 0v-5H4a1 1 0 110-2h5V4a1 1 0 011-1z" clipRule="evenodd"/>
              </svg>
              Add Tourist
            </button>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Filters */}

        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mb-6">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Search</label>
              <input
                type="text"
                placeholder="Name, ID, or nationality..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Status</label>
              <select
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              >
                <option value="All">All Status</option>
                <option value="Active">Active</option>
                <option value="In-Transit">In-Transit</option>
                <option value="SOS">SOS</option>
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Area</label>
              <select
                value={areaFilter}
                onChange={(e) => setAreaFilter(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              >
                <option value="All">All Areas</option>
                <option value="Kathmandu">Kathmandu</option>
                <option value="Pokhara">Pokhara</option>
                <option value="Lumbini">Lumbini</option>
                <option value="Chitwan">Chitwan</option>
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Safety Score</label>
              <select
                value={scoreFilter}
                onChange={(e) => setScoreFilter(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              >
                <option value="All">All Scores</option>
                <option value="High (80-100)">High (80-100)</option>
                <option value="Medium (50-79)">Medium (50-79)</option>
                <option value="Low (0-49)">Low (0-49)</option>
              </select>
            </div>
          </div>
        </div>

        {/* Tourists Table */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden">
          <div className="px-6 py-4 border-b border-gray-200">
            <h3 className="text-lg font-bold text-gray-900">
              Tourists ({filteredTourists.length})
            </h3>
          </div>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Name</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">ID</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Nationality</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Safety Score</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Current Location</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Verification</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {filteredTourists.map((tourist) => (
                  <tr
                    key={tourist.id}
                    onClick={() => handleRowClick(tourist)}
                    className="hover:bg-gray-50 cursor-pointer"
                  >
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm font-medium text-gray-900">{tourist.name}</div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-900">{tourist.id}</div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-900">{tourist.nationality}</div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${getStatusColor(tourist.status)}`}>
                        {tourist.status}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className={`text-sm font-medium ${getSafetyScoreColor(tourist.safetyScore)}`}>
                        {tourist.safetyScore}%
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-900">{tourist.currentLocation}</div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                        tourist.verification === 'Verified' ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
                      }`}>
                        {tourist.verification}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* Add Tourist Modal */}
      {showAddModal && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <h3 className="text-lg font-bold text-gray-900 mb-4">Add New Tourist</h3>
              <form onSubmit={handleAddTourist}>
                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700 mb-2">Name</label>
                  <input
                    type="text"
                    required
                    value={newTourist.name}
                    onChange={(e) => setNewTourist({...newTourist, name: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>
                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700 mb-2">Nationality</label>
                  <input
                    type="text"
                    required
                    value={newTourist.nationality}
                    onChange={(e) => setNewTourist({...newTourist, nationality: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>
                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700 mb-2">Itinerary</label>
                  <input
                    type="text"
                    value={newTourist.itinerary}
                    onChange={(e) => setNewTourist({...newTourist, itinerary: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>
                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700 mb-2">Current Location</label>
                  <input
                    type="text"
                    value={newTourist.currentLocation}
                    onChange={(e) => setNewTourist({...newTourist, currentLocation: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>
                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700 mb-2">Emergency Contacts</label>
                  <input
                    type="text"
                    value={newTourist.emergencyContacts}
                    onChange={(e) => setNewTourist({...newTourist, emergencyContacts: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="Comma separated phone numbers"
                  />
                </div>
                <div className="flex justify-end space-x-3">
                  <button
                    type="button"
                    onClick={() => setShowAddModal(false)}
                    className="px-4 py-2 bg-gray-300 text-gray-700 rounded-lg hover:bg-gray-400 transition-colors"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="px-4 py-2 bg-blue-900 text-white rounded-lg hover:bg-blue-800 transition-colors"
                  >
                    Add Tourist
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Tourist Detail Modal */}
      {showDetailModal && selectedTourist && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-10 mx-auto p-5 border w-full max-w-4xl shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <div className="flex justify-between items-center mb-6">
                <h3 className="text-2xl font-bold text-gray-900">{selectedTourist.name}</h3>
                <button
                  onClick={() => setShowDetailModal(false)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd"/>
                  </svg>
                </button>
              </div>

              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {/* KYC Information */}
                <div className="bg-gray-50 rounded-lg p-6">
                  <h4 className="text-lg font-bold text-gray-900 mb-4">KYC Information</h4>
                  <div className="space-y-3">
                    <div>
                      <span className="font-medium text-gray-700">Passport:</span>
                      <span className="ml-2 text-gray-900">{selectedTourist.kyc.passport}</span>
                    </div>
                    <div>
                      <span className="font-medium text-gray-700">Visa Expiry:</span>
                      <span className="ml-2 text-gray-900">{selectedTourist.kyc.visaExpiry}</span>
                    </div>
                    <div>
                      <span className="font-medium text-gray-700">Address:</span>
                      <span className="ml-2 text-gray-900">{selectedTourist.kyc.address}</span>
                    </div>
                  </div>
                </div>

                {/* Route Timeline */}
                <div className="bg-gray-50 rounded-lg p-6">
                  <h4 className="text-lg font-bold text-gray-900 mb-4">Route Timeline</h4>
                  <div className="space-y-3">
                    {selectedTourist.route.map((point, index) => (
                      <div key={index} className="flex items-start">
                        <div className="flex-shrink-0 w-3 h-3 bg-blue-600 rounded-full mt-1.5"></div>
                        <div className="ml-3">
                          <p className="text-sm font-medium text-gray-900">{point.location}</p>
                          <p className="text-xs text-gray-500">{point.time}</p>
                          <p className="text-xs text-gray-600">{point.status}</p>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Safety Log */}
                <div className="bg-gray-50 rounded-lg p-6 lg:col-span-2">
                  <h4 className="text-lg font-bold text-gray-900 mb-4">Safety Log</h4>
                  <div className="space-y-3">
                    {selectedTourist.safetyLog.map((log, index) => (
                      <div key={index} className="flex items-center justify-between p-3 bg-white rounded-lg">
                        <div>
                          <p className="text-sm font-medium text-gray-900">{log.event}</p>
                          <p className="text-xs text-gray-500">{log.date}</p>
                        </div>
                        <span className={`px-2 py-1 text-xs font-semibold rounded-full ${
                          log.severity === 'critical' ? 'bg-red-100 text-red-800' :
                          log.severity === 'warning' ? 'bg-yellow-100 text-yellow-800' :
                          'bg-blue-100 text-blue-800'
                        }`}>
                          {log.severity}
                        </span>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default TouristsDirectory;
