    display: 'none'
  },
  uploadButton: {
    backgroundColor: '#6c757d',
    color: 'white',
    padding: '8px 15px',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '1em',
    display: 'inline-block',
    transition: 'background-color 0.2s ease'
    // '&:hover': { // Note: See above
    //     backgroundColor: '#5a6268',
    // },
  },
  uploadedFiles: {
    marginTop: '10px',
    borderTop: '1px dashed #e9ecef',
    paddingTop: '10px'
  },
  uploadedFileItem: {
    marginBottom: '5px'
  },
  fileLink: {
    color: '#007bff',
    textDecoration: 'none',
    '&:hover': {
      // Note: See above
      textDecoration: 'underline'
    }
  },
  firButton: {
    padding: '10px 20px',
    borderRadius: '5px',
    border: 'none',
    color: 'white',
    fontSize: '1.1em',
    fontWeight: 'bold',
    cursor: 'pointer',
    transition: 'background-color 0.2s ease',
    marginTop: '10px'
  }
};
