// components/layout.jsx (This is your main layout file)
import React, { useEffect } from 'react'; // Added useEffect for ScrollToTop
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';

// Import your individual components
import Navbar from './Navbar.jsx'; // Adjust path if Navbar is in a subfolder like 'components/Navbar.jsx'
import Sidebar from './Sidebar.jsx'; // Adjust path
import Dashboard from './Dashboard.jsx'; // Adjust path
import Footer from './Footer.jsx'; // Adjust path

// Placeholder components for all routes defined in Navbar and Sidebar/Footer
// Replace these with your actual page components as you build them
const Home = () => <div className="p-4"><h2>Home Page Content</h2><p>Welcome to the home page!</p></div>;
const Tourists = () => <div className="p-4"><h2>Tourists Management</h2><p>Manage tourist data here.</p></div>;
const Incidents = () => <div className="p-4"><h2>Incidents & Alerts</h2><p>View and manage incidents.</p></div>;
const ReportsAnalytics = () => <div className="p-4"><h2>Reports & Analytics</h2><p>Detailed reports and analytics.</p></div>;
const HelpSupport = () => <div className="p-4"><h2>Help & Support</h2><p>Find help and contact support.</p></div>;
const MapGeoFencing = () => <div className="p-4"><h2>Map & Geo-fencing</h2><p>Interactive maps and geo-fencing controls.</p></div>;
const SettingsAdmin = () => <div className="p-4"><h2>Settings & Admin</h2><p>Configure portal settings.</p></div>;
const Profile = () => <div className="p-4"><h2>User Profile</h2><p>Manage your user profile.</p></div>;
const About = () => <div className="p-4"><h2>About Us</h2><p>Learn more about our portal.</p></div>;
const Contact = () => <div className="p-4"><h2>Contact Us</h2><p>Get in touch.</p></div>;
const Login = () => <div className="p-4"><h2>Login Page</h2><p>Please log in.</p></div>;

// Component to scroll to top on route change (moved here)
function ScrollToTop() {
  const { pathname } = useLocation();

  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);

  return null;
}

// Renamed from App to Layout
function Layout() {
  return (
    <Router>
      <ScrollToTop /> {/* ScrollToTop must be inside Router */}
      <div className="app-layout">
        <Sidebar /> {/* The fixed Sidebar is rendered first */}

        {/* This 'main-app-content' wrapper will push all its children
            to the right by 290px (sidebar's width) */}
        <div className="main-app-content">
          <Navbar /> {/* Your Navbar component */}
          
          {/* This div will contain the content rendered by React Router's Routes */}
          <div className="main-route-content">
            <Routes>
              {/* Define all your routes here */}
              <Route path="/" element={<Home />} />
              <Route path="/dashboard" element={<Dashboard />} />
              <Route path="/services" element={<Dashboard />} /> {/* Dashboard can be under services */}
              <Route path="/tourists" element={<Tourists />} />
              <Route path="/incidents" element={<Incidents />} />
              <Route path="/reports-analytics" element={<ReportsAnalytics />} />
              <Route path="/help-support" element={<HelpSupport />} />
              <Route path="/map-geo-fencing" element={<MapGeoFencing />} />
              <Route path="/settings-admin" element={<SettingsAdmin />} />
              <Route path="/profile" element={<Profile />} />
              <Route path="/about" element={<About />} />
              <Route path="/contact" element={<Contact />} />
              <Route path="/login" element={<Login />} />
              {/* Add more routes as needed */}
            </Routes>
          </div>

          <Footer />
        </div>
      </div>
    </Router>
  );
}

export default Layout; // Export the Layout component
