import React, { useState, useEffect } from 'react';

function ReportsAnalytics() {
  const [incidentsData, setIncidentsData] = useState([]);
  const [safetyScores, setSafetyScores] = useState([]);
  const [trendsData, setTrendsData] = useState([]);
  const [filters, setFilters] = useState({
    dateRange: 'last30days',
    location: 'all',
    incidentType: 'all'
  });

  // Simulate data fetching
  useEffect(() => {
    // Mock data for incidents
    setIncidentsData([
      { id: 1, type: 'SOS Alert', location: 'Zone A', date: '2023-10-01', status: 'Resolved' },
      { id: 2, type: 'Medical Emergency', location: 'Zone B', date: '2023-10-02', status: 'In Progress' },
      { id: 3, type: 'Weather Alert', location: 'Zone C', date: '2023-10-03', status: 'Resolved' },
    ]);

    // Mock data for safety scores
    setSafetyScores([
      { zone: 'Zone A', score: 85, trend: 'up' },
      { zone: 'Zone B', score: 92, trend: 'stable' },
      { zone: 'Zone C', score: 78, trend: 'down' },
    ]);

    // Mock data for trends
    setTrendsData([
      { month: 'Jan', incidents: 12, safetyScore: 88 },
      { month: 'Feb', incidents: 15, safetyScore: 85 },
      { month: 'Mar', incidents: 10, safetyScore: 90 },
      { month: 'Apr', incidents: 8, safetyScore: 92 },
    ]);
  }, []);

  const handleFilterChange = (e) => {
    setFilters({
      ...filters,
      [e.target.name]: e.target.value
    });
  };

  const handleExport = (format) => {
    // Mock export functionality
    console.log(`Exporting report in ${format} format`);
    alert(`Report exported as ${format.toUpperCase()}`);
  };

  return (
    <div style={styles.pageBackground}>
      <div style={styles.header}>
        <h1 style={styles.title}>Reports & Analytics</h1>
        <div style={styles.exportButtons}>
          <button onClick={() => handleExport('csv')} style={styles.exportButton}>
            Export CSV
          </button>
          <button onClick={() => handleExport('pdf')} style={styles.exportButton}>
            Export PDF
          </button>
        </div>
      </div>

      {/* Filters Section */}
      <div style={styles.filtersSection}>
        <h2 style={styles.sectionTitle}>Report Generator</h2>
        <div style={styles.filters}>
          <select name="dateRange" value={filters.dateRange} onChange={handleFilterChange} style={styles.filterSelect}>
            <option value="last7days">Last 7 Days</option>
            <option value="last30days">Last 30 Days</option>
            <option value="last3months">Last 3 Months</option>
            <option value="lastyear">Last Year</option>
          </select>
          <select name="location" value={filters.location} onChange={handleFilterChange} style={styles.filterSelect}>
            <option value="all">All Locations</option>
            <option value="zoneA">Zone A</option>
            <option value="zoneB">Zone B</option>
            <option value="zoneC">Zone C</option>
          </select>
          <select name="incidentType" value={filters.incidentType} onChange={handleFilterChange} style={styles.filterSelect}>
            <option value="all">All Incident Types</option>
            <option value="sos">SOS Alerts</option>
            <option value="medical">Medical Emergencies</option>
            <option value="weather">Weather Alerts</option>
          </select>
          <button style={styles.generateButton}>Generate Report</button>
        </div>
      </div>

      {/* Widgets/Charts Section */}
      <div style={styles.widgetsSection}>
        <h2 style={styles.sectionTitle}>Widgets/Charts</h2>
        <div style={styles.widgetsGrid}>
          {/* Incidents Chart */}
          <div style={styles.widget}>
            <h3 style={styles.widgetTitle}>Incidents Overview</h3>
            <div style={styles.chartPlaceholder}>
              <p>Chart: Incidents by Type</p>
              <div style={styles.chartData}>
                {incidentsData.map((incident) => (
                  <div key={incident.id} style={styles.incidentItem}>
                    <span>{incident.type}</span>
                    <span>{incident.status}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Safety Scores Chart */}
          <div style={styles.widget}>
            <h3 style={styles.widgetTitle}>Safety Scores</h3>
            <div style={styles.chartPlaceholder}>
              <p>Chart: Safety Scores by Zone</p>
              <div style={styles.chartData}>
                {safetyScores.map((score, index) => (
                  <div key={index} style={styles.scoreItem}>
                    <span>{score.zone}: {score.score}%</span>
                    <span style={{ color: score.trend === 'up' ? 'green' : score.trend === 'down' ? 'red' : 'gray' }}>
                      {score.trend === 'up' ? '↗' : score.trend === 'down' ? '↘' : '→'}
                    </span>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Trends Chart */}
          <div style={styles.widget}>
            <h3 style={styles.widgetTitle}>Trends</h3>
            <div style={styles.chartPlaceholder}>
              <p>Chart: Monthly Trends</p>
              <div style={styles.chartData}>
                {trendsData.map((trend, index) => (
                  <div key={index} style={styles.trendItem}>
                    <span>{trend.month}: Incidents {trend.incidents}, Safety {trend.safetyScore}%</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

// Styles
const styles = {
  pageBackground: {
    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
    backgroundColor: '#e9eff6',
    minHeight: '100vh',
    color: '#2c3e50',
    lineHeight: '1.6',
    padding: '20px',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '30px',
  },
  title: {
    fontSize: '2em',
    color: '#003366',
    margin: 0,
  },
  exportButtons: {
    display: 'flex',
    gap: '10px',
  },
  exportButton: {
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '1em',
  },
  filtersSection: {
    backgroundColor: '#ffffff',
    borderRadius: '12px',
    padding: '25px',
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.08)',
    marginBottom: '30px',
  },
  sectionTitle: {
    fontSize: '1.5em',
    color: '#003366',
    marginBottom: '20px',
  },
  filters: {
    display: 'flex',
    gap: '15px',
    flexWrap: 'wrap',
  },
  filterSelect: {
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    fontSize: '1em',
  },
  generateButton: {
    backgroundColor: '#28a745',
    color: 'white',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '5px',
    cursor: 'pointer',
