// components/layout.jsx
import React, { useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useLocation,
} from "react-router-dom";

import Navbar from "./Navbar.jsx";
import Footer from "./Footer.jsx";
import Contact from "./Contact.jsx";
import About from "./About.jsx";
import Home from "./Home.jsx";
import Dashboard from "./Dashboard.jsx";
import Login from "./Login.jsx";
import Tourists from "./TouristsDirectory.jsx";
import Incidents from "./Incidents & Alerts.jsx";
import ReportsAnalytics from "./Reports & Analytics.jsx";
import SettingsAdmin from "./Settings & Admin.jsx";
import HelpSupport from "./Help & Support.jsx";
import Profile from "./Profile.jsx";
import Header from "./Header.jsx";

function ScrollToTop() {
  const { pathname } = useLocation();
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);
  return null;
}

function Layout() {
  return (
    // Wrap everything that needs routing context inside the Router
    <Router>
      <ScrollToTop />
      <div className="app-layout">
        <Navbar />
        <div className="header">
          <Header />
        </div>

        {/* Main App Content: Contains Routes */}
        <div className="main-app-content">
          <Routes>
            <Route path="/Home" element={<Home />} />
            <Route path="/dashboard" element={<Dashboard />} />
            <Route path="/services" element={<Dashboard />} />
            <Route path="/tourists" element={<Tourists />} />
            <Route path="/incidents" element={<Incidents />} />
            <Route path="/reports-analytics" element={<ReportsAnalytics />} />
            <Route path="/help-support" element={<HelpSupport />} />
            <Route path="/settings-admin" element={<SettingsAdmin />} />
            <Route path="/profile" element={<Profile />} />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
            <Route path="/login" element={<Login />} />
          </Routes>
        </div>
        <Footer /> {/* Move Footer inside the Router, but outside the Routes */}
      </div>
    </Router>
  );
}

export default Layout;
