import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';

// --- Icon Components (for a cleaner look) ---
// You can replace these with a proper icon library like react-icons
const DashboardIcon = () => <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h18M3 14h18m-9-4v8m-7 0h14a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v8a2 2 0 002 2z" /></svg>;
const TouristsIcon = () => <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.653-.124-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.653.124-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0z" /></svg>;
const IncidentsIcon = () => <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" /></svg>;
const ReportsIcon = () => <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 17v-2m3 2v-4m3 4v-6m2 10H7a2 2 0 01-2-2V7a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" /></svg>;
const SettingsIcon = () => <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10.325 4.317c.426-1.756 2.924-1.756 3.35 0a1.724 1.724 0 002.573 1.066c1.543-.94 3.31.826 2.37 2.37a1.724 1.724 0 001.065 2.572c1.756.426 1.756 2.924 0 3.35a1.724 1.724 0 00-1.066 2.573c.94 1.543-.826 3.31-2.37 2.37a1.724 1.724 0 00-2.572 1.065c-.426 1.756-2.924 1.756-3.35 0a1.724 1.724 0 00-2.573-1.066c-1.543.94-3.31-.826-2.37-2.37a1.724 1.724 0 00-1.065-2.572c-1.756-.426-1.756-2.924 0-3.35a1.724 1.724 0 001.066-2.573c-.94-1.543.826-3.31 2.37-2.37.996.608 2.296.096 2.572-1.065zM15 12a3 3 0 11-6 0 3 3 0 016 0z" /></svg>;
const HelpIcon = () => <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8.228 9c.549-1.165 2.03-2 3.772-2 2.21 0 4 1.343 4 3 0 1.4-1.278 2.575-3.006 2.907-.542.104-.994.54-.994 1.093m0 3h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" /></svg>;
const MapIcon = () => <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0zM15 11a3 3 0 11-6 0 3 3 0 016 0z" /></svg>;
const ProfileIcon = () => <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" /></svg>;
const LogoutIcon = () => <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" /></svg>;

// --- Navigation Links Data ---
const navLinks = [
  { to: "/services", text: "Dashboard", icon: <DashboardIcon /> },
  { to: "/tourists", text: "Tourists", icon: <TouristsIcon /> },
  { to: "/incidents", text: "Incidents & Alerts", icon: <IncidentsIcon /> },
  { to: "/reports-analytics", text: "Reports & Analytics", icon: <ReportsIcon /> },
  { to: "/map-geo-fencing", text: "Map & Geo-fencing", icon: <MapIcon /> },
];

const secondaryLinks = [
    { to: "/settings-admin", text: "Settings & Admin", icon: <SettingsIcon /> },
    { to: "/help-support", text: "Help & Support", icon: <HelpIcon /> },
]

function Sidebar() {
  const navigate = useNavigate();
  const location = useLocation(); // Hook to get the current URL

  const handleLogout = () => {
    navigate('/login');
  };

  return (
    <div style={styles.sidebar}>
      <div>
        {/* --- Logo Section --- */}
        <div style={styles.logoContainer}>
          <div style={styles.logo}>
             <svg style={styles.logoIcon} fill="currentColor" viewBox="0 0 24 24"><path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z" /></svg>
          </div>
          <span style={styles.logoText}>SAHYatri</span>
        </div>

        {/* --- Navigation Links --- */}
        <nav style={styles.navigation}>
          {navLinks.map((link) => (
            <Link
              key={link.to}
              to={link.to}
              style={location.pathname.startsWith(link.to) ? styles.navLinkActive : styles.navLink}
            >
              <span style={styles.navIcon}>{link.icon}</span>
              {link.text}
            </Link>
          ))}
        </nav>
        
        <hr style={styles.divider} />
        
        <nav style={styles.navigation}>
            {secondaryLinks.map((link) => (
                <Link key={link.to} to={link.to} style={location.pathname.startsWith(link.to) ? styles.navLinkActive : styles.navLink}>
                    <span style={styles.navIcon}>{link.icon}</span>
                    {link.text}
                </Link>
            ))}
        </nav>

      </div>

      {/* --- Footer / User Section --- */}
      <div style={styles.footer}>
         <Link to="/profile" style={{ ...styles.navLink, ...styles.footerLink }}>
            <span style={styles.navIcon}><ProfileIcon /></span> Profile
         </Link>
         <button onClick={handleLogout} style={{ ...styles.navLink, ...styles.footerLink, ...styles.logoutButton }}>
            <span style={styles.navIcon}><LogoutIcon /></span> Logout
         </button>
      </div>
    </div>
  );
}

