import React from 'react';

// The component now accepts a 'className' prop.
function Footer({ className }) {
  // We combine a base class 'site-footer' with any custom className passed in.
  // The '|| '' ' ensures it doesn't break if no className is provided.
  const footerClasses = `site-footer ${className || ''}`;

  return (
    <footer className={footerClasses}>
      <div className="container">
        <p>&copy; {new Date().getFullYear()} Tourist Management System. All Rights Reserved.</p>
        {/* You can add more footer links or information here */}
      </div>
    </footer>
  );
}

// Add some basic default styles for the footer in a CSS file
// e.g., in `src/components/Footer.css` and import it.
// For now, we'll style it in Layout.css.

export default Footer;
