    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '25px',
    paddingBottom: '15px',
    borderBottom: '1px solid #e9ecef'
  },
  modalTitle: {
    fontSize: '1.8em',
    fontWeight: '700',
    color: '#003366',
    margin: '0'
  },
  modalCloseButton: {
    background: 'none',
    border: 'none',
    fontSize: '1.8em',
    cursor: 'pointer',
    color: '#6c757d',
    transition: 'color 0.2s ease'
    // '&:hover': { color: '#343a40' },
  },
  formGroup: {
    marginBottom: '20px'
  },
  formLabel: {
    display: 'block',
    fontSize: '0.9em',
    fontWeight: '500',
    color: '#495057',
    marginBottom: '8px'
  },
  formInput: {
    width: '100%',
    padding: '10px 12px',
    border: '1px solid #ced4da',
    borderRadius: '8px',
    fontSize: '1em',
    outline: 'none',
    transition: 'border-color 0.2s ease, box-shadow 0.2s ease'
    // '&:focus': { borderColor: '#007bff', boxShadow: '0 0 0 2px rgba(0,123,255,0.25)' },
  },
  modalActions: {
    display: 'flex',
    justifyContent: 'flex-end',
    gap: '12px',
    marginTop: '30px'
  },
  modalCancelButton: {
    padding: '10px 18px',
    backgroundColor: '#ced4da',
    // gray-300
    color: '#495057',
    // gray-700
    borderRadius: '8px',
    border: 'none',
    cursor: 'pointer',
    fontWeight: '500',
    transition: 'background-color 0.2s ease'
    // '&:hover': { backgroundColor: '#adb5bd' },
  },
  modalSubmitButton: {
    padding: '10px 18px',
    backgroundColor: '#003366',
    // blue-900
    color: 'white',
    borderRadius: '8px',
    border: 'none',
    cursor: 'pointer',
    fontWeight: '500',
    transition: 'background-color 0.2s ease'
    // '&:hover': { backgroundColor: '#002244' },
  },
  // --- Tourist Detail Modal Specifics ---
  detailGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(1, 1fr)',
    gap: '24px',
    // gap-6
    '@media (min-width: 1024px)': {
      // lg:grid-cols-2
      gridTemplateColumns: 'repeat(2, 1fr)'
    }
  },
  detailCard: {
    backgroundColor: '#f8f9fa',
    // bg-gray-50
    borderRadius: '10px',
    padding: '25px',
    border: '1px solid #e9ecef'
  },
  detailCardSpan2: {
    backgroundColor: '#f8f9fa',
    borderRadius: '10px',
    padding: '25px',
    border: '1px solid #e9ecef',
    gridColumn: 'span 1',
    // Default for small screens
    '@media (min-width: 1024px)': {
      // lg:col-span-2
      gridColumn: 'span 2'
    }
  },
  detailCardTitle: {
    fontSize: '1.2em',
    fontWeight: '700',
    color: '#003366',
    marginBottom: '18px'
  },
  detailInfoGroup: {
    display: 'flex',
    flexDirection: 'column',
    gap: '12px' // space-y-3
  },
  detailInfoItem: {
    display: 'flex',
    alignItems: 'center' // Align label and value nicely
  },
  detailInfoLabel: {
    fontWeight: '500',
    color: '#495057',
    minWidth: '100px' // Align labels
  },
  detailInfoValue: {
    color: '#212529',
    marginLeft: '8px' // ml-2
  },
  // Route Timeline specific styles
  timelineList: {
    display: 'flex',
    flexDirection: 'column',
    gap: '12px',
    // space-y-3
    position: 'relative',
    paddingLeft: '15px' // Space for the timeline line/dots
  },
  timelineItem: {
    display: 'flex',
    alignItems: 'flex-start',
    position: 'relative'
  },
  timelinePoint: {
    flexShrink: 0,
    width: '12px',
    // w-3
    height: '12px',
    // h-3
    backgroundColor: '#007bff',
    // bg-blue-600
    borderRadius: '50%',
    marginTop: '6px',
    // mt-1.5 to align with text
    position: 'absolute',
    left: '-22px',
    // Adjust to align with the intended line
    top: '0'
  },
  timelineContent: {
    marginLeft: '12px',
    // ml-3
    paddingLeft: '10px',
    // For visual separation from point
    borderLeft: '2px solid #e9ecef' // Simulate timeline line
  },
  timelineLocation: {
    fontSize: '0.9em',
    fontWeight: '500',
    color: '#212529',
    margin: '0'
  },
  timelineTime: {
    fontSize: '0.75em',
    color: '#6c757d',
    margin: '2px 0 0 0'
  },
  timelineStatus: {
    fontSize: '0.75em',
    color: '#495057',
    margin: '2px 0 0 0'
  },
  // The actual connecting line for the timeline would require pseudo-elements
  // or a dedicated CSS setup. This is a visual approximation.

  // Safety Log specific styles
  safetyLogList: {
    display: 'flex',
    flexDirection: 'column',
    gap: '12px' // space-y-3
  },
  safetyLogItem: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: '12px',
    // p-3
    backgroundColor: '#ffffff',
    borderRadius: '8px',
    boxShadow: '0 1px 3px rgba(0,0,0,0.05)',
    border: '1px solid #e9ecef'
  },
  safetyLogEvent: {
    fontSize: '0.9em',
    fontWeight: '500',
    color: '#212529',
    margin: '0'
  },
  safetyLogDate: {
    fontSize: '0.75em',
    color: '#6c757d',
    margin: '2px 0 0 0'
  }
};
export default TouristsDirectory;
var _c;
$RefreshReg$(_c, "TouristsDirectory");

sourcesContent:
import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";

// Mock data for demonstration
const mockTourists = [
  {
    id: 'T001',
    name: 'John Smith',
    nationality: 'United States',
    status: 'Active',
    safetyScore: 95,
    itinerary: 'Kathmandu → Pokhara → Chitwan',
    currentLocation: 'Thamel, Kathmandu',
    verification: 'Verified',
    emergencyContacts: '+1-555-0123, +1-555-0456',
    kyc: {
      passport: 'US123456789',
      visaExpiry: '2024-12-31',
      address: '123 Main St, New York, NY'
    },
    route: [
      { location: 'Tribhuvan Airport', time: '2024-01-15 10:30', status: 'Arrived' },
      { location: 'Thamel Hotel', time: '2024-01-15 12:00', status: 'Checked-in' },
      { location: 'Pashupatinath Temple', time: '2024-01-15 15:30', status: 'Visited' }
    ],
    safetyLog: [
      { date: '2024-01-15', event: 'Safe check-in completed', severity: 'info' },
      { date: '2024-01-14', event: 'Weather alert acknowledged', severity: 'warning' }
    ]
  },
  {
    id: 'T002',
    name: 'Maria Garcia',
    nationality: 'Spain',
    status: 'In-Transit',
    safetyScore: 88,
    itinerary: 'Lumbini → Kathmandu',
    currentLocation: 'Lumbini',
    verification: 'Verified',
    emergencyContacts: '+34-555-7890, +34-555-2345',
    kyc: {
      passport: 'ES987654321',
      visaExpiry: '2024-11-15',
      address: '456 Plaza Mayor, Madrid'
    },
    route: [
      { location: 'Lumbini Bus Station', time: '2024-01-15 08:00', status: 'Departed' },
      { location: 'Butwal Transit', time: '2024-01-15 11:30', status: 'In-transit' }
    ],
    safetyLog: [
      { date: '2024-01-15', event: 'Transit started', severity: 'info' }
    ]
  },
  {
    id: 'T003',
    name: 'Raj Patel',
    nationality: 'India',
    status: 'SOS',
    safetyScore: 45,
    itinerary: 'Everest Base Camp',
    currentLocation: 'Namche Bazaar',
    verification: 'Pending',
    emergencyContacts: '+91-555-6789, +91-555-0123',
    kyc: {
      passport: 'IN456789123',
      visaExpiry: '2024-10-30',
      address: '789 MG Road, Mumbai'
    },
    route: [
      { location: 'Lukla Airport', time: '2024-01-14 06:00', status: 'Arrived' },
      { location: 'Namche Bazaar', time: '2024-01-14 14:00', status: 'SOS Activated' }
    ],
    safetyLog: [
      { date: '2024-01-14', event: 'SOS Alert triggered', severity: 'critical' },
      { date: '2024-01-14', event: 'Medical assistance dispatched', severity: 'warning' }
    ]
  },
  {
    id: 'T004',
    name: 'Emma Wilson',
    nationality: 'Australia',
    status: 'Active',
    safetyScore: 92,
    itinerary: 'Pokhara Adventure',
    currentLocation: 'Phewa Lake, Pokhara',
    verification: 'Verified',
    emergencyContacts: '+61-555-3456, +61-555-7890',
    kyc: {
      passport: 'AU789123456',
      visaExpiry: '2025-01-20',
      address: '321 Queen St, Sydney'
    },
    route: [
      { location: 'Pokhara Airport', time: '2024-01-15 09:00', status: 'Arrived' },
      { location: 'Lakeside Hotel', time: '2024-01-15 10:30', status: 'Checked-in' },
      { location: 'Phewa Lake', time: '2024-01-15 14:00', status: 'Current' }
    ],
    safetyLog: [
      { date: '2024-01-15', event: 'Activity tracking active', severity: 'info' }
    ]
  }
];

function TouristsDirectory() {
  const [tourists, setTourists] = useState(mockTourists);
  const [filteredTourists, setFilteredTourists] = useState(mockTourists);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('All');
  const [areaFilter, setAreaFilter] = useState('All');
  const [scoreFilter, setScoreFilter] = useState('All');
  const [showAddModal, setShowAddModal] = useState(false);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [selectedTourist, setSelectedTourist] = useState(null);
  const [newTourist, setNewTourist] = useState({
    name: '',
    nationality: '',
    status: 'Active',
    safetyScore: 100,
    itinerary: '',
    currentLocation: '',
    emergencyContacts: ''
  });

  // Filter tourists based on search and filters
  useEffect(() => {
    let filtered = tourists;

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(tourist =>
        tourist.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        tourist.id.toLowerCase().includes(searchTerm.toLowerCase()) ||
        tourist.nationality.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Status filter
    if (statusFilter !== 'All') {
      filtered = filtered.filter(tourist => tourist.status === statusFilter);
    }

    // Area filter (based on current location)
    if (areaFilter !== 'All') {
      filtered = filtered.filter(tourist =>
        tourist.currentLocation.toLowerCase().includes(areaFilter.toLowerCase())
      );
    }

    // Safety score filter
    if (scoreFilter !== 'All') {
      const scoreRanges = {
        'High (80-100)': [80, 100],
        'Medium (50-79)': [50, 79],
        'Low (0-49)': [0, 49]
      };
      const [min, max] = scoreRanges[scoreFilter];
      filtered = filtered.filter(tourist =>
        tourist.safetyScore >= min && tourist.safetyScore <= max
      );
    }

    setFilteredTourists(filtered);
  }, [tourists, searchTerm, statusFilter, areaFilter, scoreFilter]);

  const handleRowClick = (tourist) => {
    setSelectedTourist(tourist);
    setShowDetailModal(true);
  };

  const handleAddTourist = (e) => {
    e.preventDefault();
    const tourist = {
      id: `T${String(tourists.length + 1).padStart(3, '0')}`,
      ...newTourist,
      verification: 'Pending',
      kyc: {
        passport: 'Pending',
        visaExpiry: 'Pending',
        address: 'Pending'
      },
      route: [],
      safetyLog: []
    };
    setTourists([...tourists, tourist]);
    setShowAddModal(false);
    setNewTourist({
      name: '',
      nationality: '',
      status: 'Active',
      safetyScore: 100,
      itinerary: '',
      currentLocation: '',
      emergencyContacts: ''
    });
  };

  const getStatusStyle = (status) => {
    switch (status) {
      case 'Active': return { backgroundColor: '#e6ffed', color: '#28a745' }; // bg-green-100, text-green-800
      case 'In-Transit': return { backgroundColor: '#e0f2ff', color: '#007bff' }; // bg-blue-100, text-blue-800
      case 'SOS': return { backgroundColor: '#ffebe6', color: '#dc3545' }; // bg-red-100, text-red-800
      default: return { backgroundColor: '#f0f4f8', color: '#495057' }; // bg-gray-100, text-gray-800
    }
  };

  const getSafetyScoreColor = (score) => {
    if (score >= 80) return { color: '#28a745' }; // text-green-600
    if (score >= 50) return { color: '#ffc107' }; // text-yellow-600
    return { color: '#dc3545' }; // text-red-600
  };

  const getVerificationStyle = (verification) => {
    if (verification === 'Verified') return { backgroundColor: '#e6ffed', color: '#28a745' };
    return { backgroundColor: '#fffbe6', color: '#ffc107' };
  };

  const getSeverityStyle = (severity) => {
    switch (severity) {
      case 'critical': return { backgroundColor: '#ffebe6', color: '#dc3545' };
      case 'warning': return { backgroundColor: '#fffbe6', color: '#ffc107' };
      case 'info': return { backgroundColor: '#e0f2ff', color: '#007bff' };
      default: return { backgroundColor: '#f0f4f8', color: '#495057' };
    }
  };

  return (
    <div style={styles.pageBackground}>
      {/* Dashboard Header - Full Width (Standardized) */}
      

      <div style={styles.mainContentWrapper}>
        {/* Tourists Directory Section Header (Page Title Area) */}
        <div style={styles.pageHeader}>
            <h1 style={styles.pageTitleText}>Tourists Directory</h1>
            <button
              onClick={() => setShowAddModal(true)}
              style={styles.addTouristButton}
            >
              <svg style={styles.addTouristIcon} fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M10 3a1 1 0 011 1v5h5a1 1 0 110 2h-5v5a1 1 0 11-2 0v-5H4a1 1 0 110-2h5V4a1 1 0 011-1z" clipRule="evenodd"/>
              </svg>
              Add Tourist
            </button>
        </div>

        {/* Filters */}
        <div style={styles.filterCard}>
          <div style={styles.filterGrid}>
            <div>
              <label style={styles.filterLabel}>Search</label>
              <input
                type="text"
                placeholder="Name, ID, or nationality..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                style={styles.filterInput}
              />
            </div>
            <div>
              <label style={styles.filterLabel}>Status</label>
              <select
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
                style={styles.filterSelect}
              >
                <option value="All">All Status</option>
                <option value="Active">Active</option>
                <option value="In-Transit">In-Transit</option>
                <option value="SOS">SOS</option>
              </select>
            </div>
            <div>
              <label style={styles.filterLabel}>Area</label>
              <select
                value={areaFilter}
                onChange={(e) => setAreaFilter(e.target.value)}
                style={styles.filterSelect}
              >
                <option value="All">All Areas</option>
                <option value="Kathmandu">Kathmandu</option>
                <option value="Pokhara">Pokhara</option>
                <option value="Lumbini">Lumbini</option>
                <option value="Chitwan">Chitwan</option>
              </select>
            </div>
            <div>
              <label style={styles.filterLabel}>Safety Score</label>
              <select
                value={scoreFilter}
                onChange={(e) => setScoreFilter(e.target.value)}
                style={styles.filterSelect}
              >
                <option value="All">All Scores</option>
                <option value="High (80-100)">High (80-100)</option>
                <option value="Medium (50-79)">Medium (50-79)</option>
                <option value="Low (0-49)">Low (0-49)</option>
              </select>
            </div>
          </div>
        </div>

        {/* Tourists Table */}
        <div style={styles.tableCard}>
          <div style={styles.tableCardHeader}>
            <h3 style={styles.cardTitle}>
              Tourists ({filteredTourists.length})
            </h3>
          </div>
          <div style={styles.tableContainer}>
            <table style={styles.table}>
              <thead>
                <tr>
                  <th style={styles.th}>Name</th>
                  <th style={styles.th}>ID</th>
                  <th style={styles.th}>Nationality</th>
                  <th style={styles.th}>Status</th>
                  <th style={styles.th}>Safety Score</th>
                  <th style={styles.th}>Current Location</th>
                  <th style={styles.th}>Verification</th>
                </tr>
              </thead>
              <tbody>
                {filteredTourists.map((tourist) => (
                  <tr
                    key={tourist.id}
                    onClick={() => handleRowClick(tourist)}
                    style={styles.tr} // Hover effect not possible with inline styles directly
                  >
                    <td style={styles.td}>
                      <div style={styles.tdPrimaryText}>{tourist.name}</div>
                    </td>
                    <td style={styles.td}>
                      <div style={styles.tdSecondaryText}>{tourist.id}</div>
                    </td>
                    <td style={styles.td}>
                      <div style={styles.tdSecondaryText}>{tourist.nationality}</div>
                    </td>
                    <td style={styles.td}>
                      <span style={{...styles.statusBadge, ...getStatusStyle(tourist.status)}}>
                        {tourist.status}
                      </span>
                    </td>
                    <td style={styles.td}>
                      <div style={{...styles.tdPrimaryText, ...getSafetyScoreColor(tourist.safetyScore)}}>
                        {tourist.safetyScore}%
                      </div>
                    </td>
                    <td style={styles.td}>
                      <div style={styles.tdSecondaryText}>{tourist.currentLocation}</div>
                    </td>
                    <td style={styles.td}>
                      <span style={{...styles.statusBadge, ...getVerificationStyle(tourist.verification)}}>
                        {tourist.verification}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* Add Tourist Modal */}
      {showAddModal && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContentSmall}>
            <div style={styles.modalHeader}>
              <h3 style={styles.modalTitle}>Add New Tourist</h3>
              <button
                onClick={() => setShowAddModal(false)}
                style={styles.modalCloseButton}
              >
                &times;
              </button>
            </div>
            <form onSubmit={handleAddTourist}>
              <div style={styles.formGroup}>
                <label style={styles.formLabel}>Name</label>
                <input
                  type="text"
                  required
                  value={newTourist.name}
                  onChange={(e) => setNewTourist({...newTourist, name: e.target.value})}
                  style={styles.formInput}
                />
              </div>
              <div style={styles.formGroup}>
                <label style={styles.formLabel}>Nationality</label>
                <input
                  type="text"
                  required
                  value={newTourist.nationality}
                  onChange={(e) => setNewTourist({...newTourist, nationality: e.target.value})}
                  style={styles.formInput}
                />
              </div>
              <div style={styles.formGroup}>
                <label style={styles.formLabel}>Itinerary</label>
                <input
                  type="text"
                  value={newTourist.itinerary}
                  onChange={(e) => setNewTourist({...newTourist, itinerary: e.target.value})}
                  style={styles.formInput}
                />
              </div>
              <div style={styles.formGroup}>
                <label style={styles.formLabel}>Current Location</label>
                <input
                  type="text"
                  value={newTourist.currentLocation}
                  onChange={(e) => setNewTourist({...newTourist, currentLocation: e.target.value})}
                  style={styles.formInput}
                />
              </div>
              <div style={styles.formGroup}>
                <label style={styles.formLabel}>Emergency Contacts</label>
                <input
                  type="text"
                  value={newTourist.emergencyContacts}
                  onChange={(e) => setNewTourist({...newTourist, emergencyContacts: e.target.value})}
                  style={styles.formInput}
                  placeholder="Comma separated phone numbers"
                />
              </div>
              <div style={styles.modalActions}>
                <button
                  type="button"
                  onClick={() => setShowAddModal(false)}
                  style={styles.modalCancelButton}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  style={styles.modalSubmitButton}
                >
                  Add Tourist
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Tourist Detail Modal */}
      {showDetailModal && selectedTourist && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContentLarge}>
            <div style={styles.modalHeader}>
              <h3 style={styles.modalTitle}>{selectedTourist.name}</h3>
              <button
                onClick={() => setShowDetailModal(false)}
                style={styles.modalCloseButton}
              >
                &times;
              </button>
            </div>

            <div style={styles.detailGrid}>
              {/* KYC Information */}
              <div style={styles.detailCard}>
                <h4 style={styles.detailCardTitle}>KYC Information</h4>
                <div style={styles.detailInfoGroup}>
                  <div style={styles.detailInfoItem}>
                    <span style={styles.detailInfoLabel}>Passport:</span>
                    <span style={styles.detailInfoValue}>{selectedTourist.kyc.passport}</span>
                  </div>
                  <div style={styles.detailInfoItem}>
                    <span style={styles.detailInfoLabel}>Visa Expiry:</span>
                    <span style={styles.detailInfoValue}>{selectedTourist.kyc.visaExpiry}</span>
                  </div>
                  <div style={styles.detailInfoItem}>
                    <span style={styles.detailInfoLabel}>Address:</span>
                    <span style={styles.detailInfoValue}>{selectedTourist.kyc.address}</span>
                  </div>
                </div>
              </div>

              {/* Route Timeline */}
              <div style={styles.detailCard}>
                <h4 style={styles.detailCardTitle}>Route Timeline</h4>
                <div style={styles.timelineList}>
                  {selectedTourist.route.map((point, index, array) => (
                    <div key={index} style={{
                      ...styles.timelineItem,
                      // Pseudo-elements would be needed for the actual circle and line
                      // For inline styles, this represents visual intent.
                      // Use a CSS file for `::before` and `::after`
                      // Also for `timelineItemLast`
                      ...(index === array.length - 1 ? { /* styles.timelineItemLast */ } : {})
                    }}>
                      <div style={styles.timelinePoint}></div> {/* Visual representation of the point */}
                      <div style={styles.timelineContent}>
                        <p style={styles.timelineLocation}>{point.location}</p>
                        <p style={styles.timelineTime}>{point.time}</p>
                        <p style={styles.timelineStatus}>{point.status}</p>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* Safety Log */}
              <div style={styles.detailCardSpan2}>
                <h4 style={styles.detailCardTitle}>Safety Log</h4>
                <div style={styles.safetyLogList}>
                  {selectedTourist.safetyLog.map((log, index) => (
                    <div key={index} style={styles.safetyLogItem}>
                      <div>
                        <p style={styles.safetyLogEvent}>{log.event}</p>
                        <p style={styles.safetyLogDate}>{log.date}</p>
                      </div>
                      <span style={{...styles.statusBadge, ...getSeverityStyle(log.severity)}}>
                        {log.severity}
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

// --- Professional Government Website Styles (Standardized) ---
const styles = {
    // --- Shared Base Styles ---
    pageBackground: {
        fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
