// components/layout.jsx
import React, { useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useLocation,
} from "react-router-dom";

import Navbar from "./Navbar.jsx";
import Footer from "./Footer.jsx";
import Contact from "./Contact.jsx";
import About from "./About.jsx";
import Home from "./Home.jsx";
import Dashboard from "./Dashboard.jsx";
import Services from "./Services.jsx";
import Login from "./Login.jsx";
import Tourists from "./TouristsDirectory.jsx";
import Incidents from "./Incidents & Alerts.jsx";
import ReportsAnalytics from "./Reports & Analytics.jsx";
import SettingsAdmin from "./Settings & Admin.jsx";
import HelpSupport from "./Help & Support.jsx";
import Profile from "./Profile.jsx";
import Header from "./Header.jsx";
import MainLayout from "./mainlayout.jsx";

function ScrollToTop() {
  const { pathname } = useLocation();
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);
  return null;
}

function HomeLayout() {
  return (
    // Wrap everything that needs routing context inside the Router
    <>
      // Wrap everything that needs routing context inside the Router
      <div className="route">
        <Routes>
          {/* <Route path="/Home" element={<Home />} /> */}
          <Route path="/" element={<Home />} />

        </Routes>
      </div><div class="grid grid-cols-[1fr] grid-rows-[100px_1fr_100px] gap-y-[10px] gap-x-[10px]">
        <Home />
      </div>
      </>
  );
}

export default HomeLayout;
