// components/layout.jsx
import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';

// Import your individual components (ensure paths are correct, e.g., './Navbar.jsx' if in same folder)
import Navbar from './Navbar.jsx';
import Sidebar from './Sidebar.jsx';
import Dashboard from './Dashboard.jsx';
import Footer from './Footer.jsx';

// Placeholder components for all routes defined in Navbar and Sidebar/Footer
// **IMPORTANT**: Replace these with your actual component imports as you create them!
const Home = () => <div className="p-4 bg-white rounded shadow-md mt-4"><h2>Home Page Content</h2><p>Welcome to the home page! Navigate using the sidebar.</p></div>;
const Tourists = () => <div className="p-4 bg-white rounded shadow-md mt-4"><h2>Tourists Management</h2><p>Manage tourist data here.</p></div>;
const Incidents = () => <div className="p-4 bg-white rounded shadow-md mt-4"><h2>Incidents & Alerts</h2><p>View and manage incidents.</p></div>;
const ReportsAnalytics = () => <div className="p-4 bg-white rounded shadow-md mt-4"><h2>Reports & Analytics</h2><p>Detailed reports and analytics.</p></div>;
const HelpSupport = () => <div className="p-4 bg-white rounded shadow-md mt-4"><h2>Help & Support</h2><p>Find help and contact support.</p></div>;
const MapGeoFencing = () => <div className="p-4 bg-white rounded shadow-md mt-4"><h2>Map & Geo-fencing</h2><p>Interactive maps and geo-fencing controls.</p></div>;
const SettingsAdmin = () => <div className="p-4 bg-white rounded shadow-md mt-4"><h2>Settings & Admin</h2><p>Configure portal settings.</p></div>;
const Profile = () => <div className="p-4 bg-white rounded shadow-md mt-4"><h2>User Profile</h2><p>Manage your user profile.</p></div>;
const About = () => <div className="p-4 bg-white rounded shadow-md mt-4"><h2>About Us</h2><p>Learn more about our portal.</p></div>;
const Contact = () => <div className="p-4 bg-white rounded shadow-md mt-4"><h2>Contact Us</h2><p>Get in touch.</p></div>;
const Login = () => <div className="p-4 bg-white rounded shadow-md mt-4"><h2>Login Page</h2><p>Please log in.</p></div>;


// Component to scroll to top on route change
function ScrollToTop() {
  const { pathname } = useLocation();
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);
  return null;
}

// Renamed from App to Layout
function Layout() {
  return (
    <Router>
      <ScrollToTop /> {/* ScrollToTop must be inside Router */}
      {/* The main app container for the entire layout */}
      <div className="app-layout">
        {/*
          1. Sidebar: Fixed to the left edge of the viewport.
             It defines its own fixed width and height with inline styles.
        */}
        <Header />

        {/*
          2. Main App Content: This div holds everything else (Navbar, Routes, Footer).
             It's offset by margin-left to make space for the fixed sidebar.
             It stretches to fill the remaining horizontal space.
             It's a flex column to stack its children vertically.
        */}
        <div className="main-app-content">
          {/*
            3. Navbar: Renders at the top of the 'main-app-content' area.
               Its Tailwind classes (sticky top-0 z-50) make it sticky within this area.
               Its content starts next to the sidebar.
          */}
          <Navbar />
          
          {/*
            4. Main Route Content: This is where pages (like Dashboard) are rendered.
               It takes up all available vertical space (flex: 1) and scrolls independently.
          */}
          <div className="main-route-content">
            <Routes>
              {/* Define all your application routes here */}
              <Route path="/" element={<Home />} />
              <Route path="/dashboard" element={<Dashboard />} />
              <Route path="/services" element={<Dashboard />} /> {/* Assuming Dashboard is part of services */}
              <Route path="/tourists" element={<Tourists />} />
              <Route path="/incidents" element={<Incidents />} />
              <Route path="/reports-analytics" element={<ReportsAnalytics />} />
              <Route path="/help-support" element={<HelpSupport />} />
              <Route path="/map-geo-fencing" element={<MapGeoFencing />} />
              <Route path="/settings-admin" element={<SettingsAdmin />} />
              <Route path="/profile" element={<Profile />} />
              <Route path="/about" element={<About />} />
              <Route path="/contact" element={<Contact />} />
              <Route path="/login" element={<Login />} />
              {/* Add more specific routes as necessary */}
            </Routes>
          </div>

          {/*
            5. Footer: Renders at the bottom of the 'main-app-content' area.
               Its Tailwind classes (mt-auto) and the flex-column parent push it to the bottom.
               Its content is entirely visible next to the sidebar.
          */}
          <Footer />
        </div>
      </div>
    </Router>
  );
}

export default Layout;
