import React, { useState, useEffect } from 'react';

function SettingsAdmin() {
  const [users, setUsers] = useState([
    { id: 1, name: 'Admin User', role: 'Administrator', status: 'Active' },
    { id: 2, name: 'Operator 1', role: 'Operator', status: 'Active' },
    { id: 3, name: 'Viewer 1', role: 'Viewer', status: 'Inactive' },
  ]);
  const [newUser, setNewUser] = useState({ name: '', role: 'Viewer', status: 'Active' });
  const [editingUser, setEditingUser] = useState(null);
  const [language, setLanguage] = useState('en');
  const [auditLogs, setAuditLogs] = useState([
    { id: 1, action: 'User Login', user: 'Admin User', timestamp: '2023-10-01 10:00:00' },
    { id: 2, action: 'Data Purge', user: 'Admin User', timestamp: '2023-10-01 09:30:00' },
    { id: 3, action: 'Role Change', user: 'Operator 1', timestamp: '2023-10-01 08:45:00' },
  ]);

  // Language options
  const languages = [
    { code: 'en', name: 'English' },
    { code: 'hi', name: 'Hindi' },
    { code: 'mr', name: 'Marathi' },
    { code: 'gu', name: 'Gujarati' },
  ];

  const handleAddUser = () => {
    if (newUser.name.trim()) {
      const user = { ...newUser, id: users.length + 1 };
      setUsers([...users, user]);
      setNewUser({ name: '', role: 'Viewer', status: 'Active' });
    }
  };

  const handleEditUser = (user) => {
    setEditingUser(user);
    setNewUser({ name: user.name, role: user.role, status: user.status });
  };

  const handleUpdateUser = () => {
    if (editingUser && newUser.name.trim()) {
      const updatedUsers = users.map(u => u.id === editingUser.id ? { ...u, ...newUser } : u);
      setUsers(updatedUsers);
      setEditingUser(null);
      setNewUser({ name: '', role: 'Viewer', status: 'Active' });
    }
  };

  const handleDeleteUser = (id) => {
    setUsers(users.filter(u => u.id !== id));
  };

  const handleLanguageChange = (lang) => {
    setLanguage(lang);
    // Simulate instant language toggle
    console.log(`Language changed to: ${lang}`);
  };

  const handleDataPurge = () => {
    // Mock data purge
    setAuditLogs([]);
    console.log('Data purged');
    alert('Data has been purged successfully.');
  };

  return (
    <div style={styles.pageBackground}>
      <div style={styles.header}>
        <h1 style={styles.title}>Settings & Admin</h1>
      </div>

      {/* User Management Section */}
      <div style={styles.section}>
        <h2 style={styles.sectionTitle}>User Management</h2>
        <div style={styles.userForm}>
          <input
            type="text"
            placeholder="User Name"
            value={newUser.name}
            onChange={(e) => setNewUser({ ...newUser, name: e.target.value })}
            style={styles.input}
          />
          <select
            value={newUser.role}
            onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}
            style={styles.select}
          >
            <option value="Administrator">Administrator</option>
            <option value="Operator">Operator</option>
            <option value="Viewer">Viewer</option>
          </select>
          <select
            value={newUser.status}
            onChange={(e) => setNewUser({ ...newUser, status: e.target.value })}
            style={styles.select}
          >
            <option value="Active">Active</option>
            <option value="Inactive">Inactive</option>
          </select>
          <button
            onClick={editingUser ? handleUpdateUser : handleAddUser}
            style={styles.button}
          >
            {editingUser ? 'Update User' : 'Add User'}
          </button>
        </div>
        <div style={styles.userList}>
          {users.map((user) => (
            <div key={user.id} style={styles.userItem}>
              <span>{user.name}</span>
              <span>{user.role}</span>
              <span>{user.status}</span>
              <div style={styles.userActions}>
                <button onClick={() => handleEditUser(user)} style={styles.editButton}>
                  Edit
                </button>
                <button onClick={() => handleDeleteUser(user.id)} style={styles.deleteButton}>
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Language Preferences Section */}
      <div style={styles.section}>
        <h2 style={styles.sectionTitle}>Language Preferences</h2>
        <div style={styles.languageOptions}>
          {languages.map((lang) => (
            <button
              key={lang.code}
              onClick={() => handleLanguageChange(lang.code)}
              style={{
                ...styles.languageButton,
                backgroundColor: language === lang.code ? '#007bff' : '#f8f9fa',
                color: language === lang.code ? 'white' : '#333',
              }}
            >
              {lang.name}
            </button>
          ))}
        </div>
        <p style={styles.languageNote}>Current Language: {languages.find(l => l.code === language)?.name}</p>
      </div>

      {/* Privacy & Security Section */}
      <div style={styles.section}>
        <h2 style={styles.sectionTitle}>Privacy & Security</h2>
        <div style={styles.securityOptions}>
          <button onClick={handleDataPurge} style={styles.purgeButton}>
            Purge All Data
          </button>
          <div style={styles.auditLogs}>
            <h3 style={styles.subTitle}>Audit Logs</h3>
            {auditLogs.map((log) => (
              <div key={log.id} style={styles.logItem}>
                <span>{log.action}</span>
                <span>{log.user}</span>
                <span>{log.timestamp}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

// Styles
const styles = {
  pageBackground: {
    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
    backgroundColor: '#e9eff6',
    minHeight: '100vh',
    color: '#2c3e50',
    lineHeight: '1.6',
    padding: '20px',
  },
  header: {
    marginBottom: '30px',
  },
  title: {
    fontSize: '2em',
    color: '#003366',
    margin: 0,
  },
  section: {
    backgroundColor: '#ffffff',
    borderRadius: '12px',
    padding: '25px',
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.08)',
    marginBottom: '30px',
  },
  sectionTitle: {
    fontSize: '1.5em',
    color: '#003366',
    marginBottom: '20px',
  },
  userForm: {
    display: 'flex',
    gap: '15px',
    flexWrap: 'wrap',
    marginBottom: '20px',
  },
  input: {
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    fontSize: '1em',
    flex: '1',
    minWidth: '200px',
  },
  select: {
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    fontSize: '1em',
    minWidth: '150px',
  },
  button: {
    backgroundColor: '#28a745',
    color: 'white',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '1em',
  },
  userList: {
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
  },
  userItem: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '10px',
    border: '1px solid #ddd',
    borderRadius: '5px',
    backgroundColor: '#f8f9fa',
  },
  userActions: {
    display: 'flex',
    gap: '10px',
  },
  editButton: {
    backgroundColor: '#ffc107',
    color: 'white',
    border: 'none',
    padding: '5px 10px',
    borderRadius: '3px',
    cursor: 'pointer',
  },
  deleteButton: {
    backgroundColor: '#dc3545',
    color: 'white',
    border: 'none',
    padding: '5px 10px',
    borderRadius: '3px',
    cursor: 'pointer',
  },
  languageOptions: {
    display: 'flex',
    gap: '10px',
    flexWrap: 'wrap',
  },
