import React, { useState, useEffect } from "react";
import Header from "./Header"; // Make sure the path to your Header component is correct

function Dashboard() {
  const [activeTourists, setActiveTourists] = useState(1247);
  const [issuedIDs, setIssuedIDs] = useState(8934);
  const [currentIncidents, setCurrentIncidents] = useState(12);
  const [openSOS, setOpenSOS] = useState(3);

  // Simulate real-time updates
  useEffect(() => {
    const interval = setInterval(() => {
      setActiveTourists((prev) => prev + Math.floor(Math.random() * 3) - 1);
      setIssuedIDs((prev) => prev + Math.floor(Math.random() * 2));
      setCurrentIncidents((prev) =>
        Math.max(0, prev + Math.floor(Math.random() * 3) - 1)
      );
      setOpenSOS((prev) =>
        Math.max(0, prev + Math.floor(Math.random() * 2) - 1)
      );
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div style={styles.pageBackground}>
      {/* The Header component is rendered here at the top of the page */}
      <Header />

      <div style={styles.mainContentWrapper}>
        {/* Statistics Cards */}
        <div style={styles.statisticsContainer}>
          <div style={styles.statCard}>
            <div style={styles.statContent}>
              <div>
                <p style={styles.statLabel}>Active Tourists</p>
                <p style={styles.statValue}>
                  {activeTourists.toLocaleString()}
                </p>
                <p style={{ ...styles.statChange, color: '#28a745' }}>
                  +2.5% from yesterday
                </p>
              </div>
              <div style={styles.statIconBlue}>
                <svg style={styles.icon} fill="currentColor" viewBox="0 0 20 20">
                  <path d="M13 6a3 3 0 11-6 0 3 3 0 016 0zM18 8a2 2 0 11-4 0 2 2 0 014 0zM14 15a4 4 0 00-8 0v3h8v-3z" />
                  <path d="M6 8a2 2 0 11-4 0 2 2 0 014 0zM16 18v-3a5.972 5.972 0 00-.75-2.906A3.005 3.005 0 0119 15v3h-3zM4.75 12.094A5.973 5.973 0 004 15v3H1v-3a3 3 0 013.75-2.906z" />
                </svg>
              </div>
            </div>
          </div>

          <div style={styles.statCard}>
            <div style={styles.statContent}>
              <div>
                <p style={styles.statLabel}>Issued IDs</p>
                <p style={{ ...styles.statValue, color: '#218838' }}>
                  {issuedIDs.toLocaleString()}
                </p>
                <p style={{ ...styles.statChange, color: '#28a745' }}>
                  +12% from last week
                </p>
              </div>
              <div style={styles.statIconGreen}>
                <svg style={styles.icon} fill="currentColor" viewBox="0 0 20 20">
                  <path d="M4 4a2 2 0 00-2 2v1h16V6a2 2 0 00-2-2H4zM18 9H2v5a2 2 0 002 2h12a2 2 0 002-2V9zM4 13a1 1 0 011-1h1a1 1 0 110 2H5a1 1 0 01-1-1zm5-1a1 1 0 100 2h1a1 1 0 100-2H9z" />
                </svg>
              </div>
            </div>
          </div>

          <div style={styles.statCard}>
            <div style={styles.statContent}>
              <div>
                <p style={styles.statLabel}>Current Incidents</p>
                <p style={{ ...styles.statValue, color: '#e0a800' }}>
                  {currentIncidents}
                </p>
                <p style={{ ...styles.statChange, color: '#fd7e14' }}>
                  3 resolved today
                </p>
              </div>
              <div style={styles.statIconOrange}>
                <svg style={styles.icon} fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                </svg>
              </div>
            </div>
          </div>

          <div style={styles.statCard}>
            <div style={styles.statContent}>
              <div>
                <p style={styles.statLabel}>Open SOS</p>
                <p style={{ ...styles.statValue, color: '#dc3545' }}>
                  {openSOS}
                </p>
                <p style={{ ...styles.statChange, color: '#dc3545' }}>
                  Requires immediate attention
                </p>
              </div>
              <div style={styles.statIconRed}>
                <svg style={styles.icon} fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M12 1.586l-4 4v12.828l4-4V1.586zM3.707 3.293A1 1 0 002 4v10a1 1 0 00.293.707L6 18.414V5.586L3.707 3.293zM17.707 5.293L14 1.586v12.828l2.293 2.293A1 1 0 0018 16V6a1 1 0 00-.293-.707z" clipRule="evenodd" />
                </svg>
              </div>
            </div>
          </div>
        </div>

        {/* The rest of your dashboard content (Map, Feed, etc.) remains here */}
        {/* ... */}
      </div>
    </div>
  );
}

// --- Styles ---
const styles = {
    pageBackground: {
        fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
        backgroundColor: '#e9eff6',
        minHeight: '100vh',
        color: '#2c3e50',
    },
    mainContentWrapper: {
        maxWidth: '1400px',
        margin: '0 auto',
        padding: '30px 20px',
    },
    statisticsContainer: {
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
        gap: '20px',
        marginBottom: '30px',
    },
    statCard: {
        backgroundColor: '#ffffff',
        borderRadius: '12px',
        padding: '25px',
        boxShadow: '0 4px 10px rgba(0, 0, 0, 0.08)',
        border: '1px solid #dcdcdc',
    },
    statContent: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
    },
    statLabel: {
        fontSize: '0.9em',
        color: '#6c757d',
        margin: '0 0 5px 0',
        textTransform: 'uppercase',
    },
    statValue: {
        fontSize: '2.2em',
        fontWeight: 'bold',
        color: '#003366',
        margin: '0',
    },
    statChange: {
        fontSize: '0.85em',
        color: '#6c757d',
        margin: '5px 0 0 0',
    },
    statIconBlue: { backgroundColor: '#e0f2ff', borderRadius: '50%', padding: '12px', display: 'flex', color: '#007bff' },
    statIconGreen: { backgroundColor: '#e6ffed', borderRadius: '50%', padding: '12px', display: 'flex', color: '#28a745' },
    statIconOrange: { backgroundColor: '#fffbe6', borderRadius: '50%', padding: '12px', display: 'flex', color: '#ffc107' },
    statIconRed: { backgroundColor: '#ffebe6', borderRadius: '50%', padding: '12px', display: 'flex', color: '#dc3545' },
    icon: {
        width: '28px',
        height: '28px',
    },
    // ... all other styles for the map, feed, and quick actions remain the same
};

export default Dashboard;
